import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import LoadingScreen from './LoadingScreen';
import Summary from './Summary'; // Import Summary component

function App() {
  // State variables to manage file selection, loading state, and summary data
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState(null);

  // Function to handle file input change
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Function to handle file upload
  const handleUpload = () => {
    // Check if a file is selected
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    // Set loading state to true
    setLoading(true);

    // Create FormData object to send file data
    const formData = new FormData();
    formData.append('file', selectedFile);

    // Send POST request to upload file
    axios.post('http://localhost:5000/upload', formData)
      .then(response => {
        // Handle successful response
        const summaryData = response.data;
        setSummary(summaryData); // Update summary state with response data
        setLoading(false); // Set loading state to false
      })
      .catch(error => {
        // Handle error
        console.error('Error:', error);
        setLoading(false); // Set loading state to false
        alert('File upload failed!'); // Show alert for failed upload
      });
  };

  return (
    <div className="App">
      {loading ? ( // If loading is true, display loading screen
        <LoadingScreen />
      ) : (
        <> {/* Fragment shorthand */}
          <header className="App-header">
            <h1>Briefly AI</h1>
            <p>Effortlessly Summarize</p>
          </header>
          <div className="button-container">
            <input
              type="file"
              id="fileInput"
              className="file-input"
              onChange={handleFileChange} // Call handleFileChange on file input change
            />
            <button className="button" onClick={() => document.getElementById('fileInput').click()}>
              Choose File
            </button>
            {selectedFile && ( // If a file is selected, display upload button
              <button className="button" onClick={handleUpload}>
                Upload File
              </button>
            )}
          </div>
          {summary && ( // If summary data exists, display Summary component
            <Summary summary={summary} />
          )}
        </>
      )}
    </div>
  );
}

export default App;
