// App.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import axios from 'axios';
import './App.css';
import Loading from './Loading'; // Import Loading component
import Summary from './Summary'; // Import Summary component

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [summary, setSummary] = useState(null); // State to store the summary

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', selectedFile);

    axios.post('http://localhost:5000/upload', formData)
      .then(response => {
        // Handle successful response
        const { summary } = response.data;
        setSummary(summary); // Set the summary in the state
        setLoading(false);
      })
      .catch(error => {
        // Handle error
        console.error('Error:', error);
        setLoading(false);
        alert('File upload failed!');
      });
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>Briefly AI</h1>
          <p>Effortlessly Summarize</p>
        </header>
        <Routes>
          <Route exact path="/" element={<div className="button-container">
            <input
              type="file"
              id="fileInput"
              className="file-input"
              onChange={handleFileChange}
            />
            <button className="button" onClick={() => document.getElementById('fileInput').click()}>
              Choose File
            </button>
            {selectedFile && (
              <button className="button" onClick={handleUpload}>
                Upload File
              </button>
            )}
          </div>} />
          <Route path="/summary" element={<Summary summary={summary} />} />
          {loading && <Route path="/loading" element={<Loading />} />} {/* Conditionally render Loading component */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
