import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData);
      // Handle successful response
      setLoading(false);
      // Redirect to summary page with the summary data (implementation depends on your framework)
      window.location.href = `/summary?summary=${response.data.summary}`; // Assuming response.data.summary holds the summary
    } catch (error) {
      // Handle error
      console.error('Error:', error);
      setLoading(false);
      alert('File upload failed!');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Briefly AI</h1>
        <p>Effortlessly Summarize</p>
      </header>
      <div className="button-container">
        <input
          type="file"
          id="fileInput"
          className="file-input"
          onChange={handleFileChange}
        />
        <button className="button" onClick={handleUpload}>
          Upload File
        </button>
      </div>
      {loading && <div className="loading">Loading...</div>}
    </div>
  );
}

export default App;
