import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [summary, setSummary] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    axios.post('http://localhost:5000/upload', formData)
      .then(response => {
        const { summary } = response.data;
        setSummary(summary); // Update state with summary text
      })
      .catch(error => {
        console.error('Error:', error);
        alert('File upload failed!');
      });
  };

  return (
    React.createElement('div', { className: 'App' },
      React.createElement('header', { className: 'App-header' },
        React.createElement('h1', null, 'Briefly AI'),
        React.createElement('p', null, 'Effortlessly Summarize')
      ),
      React.createElement('div', { className: 'button-container' },
        React.createElement('input', {
          type: 'file',
          id: 'fileInput',
          className: 'file-input',
          onChange: handleFileChange
        }),
        React.createElement('button', {
          className: 'button',
          onClick: () => document.getElementById('fileInput').click()
        }, 'Choose File'),
        selectedFile && React.createElement('button', {
          className: 'button',
          onClick: handleUpload
        }, 'Upload File')
      ),
      React.createElement('p', null, 'PDF or DOCX only'), // Move this line outside the button-container div
      summary && (
        React.createElement('div', { className: 'summary' },
          React.createElement('h2', null, 'Summary'),
          React.createElement('p', null, summary)
        )
      )
    )
  );
}

export default App;
