import React, { useState, useRef } from 'react';
import axios from 'axios';
import './App.css';
import ProgressBar from 'progressbar.js'; // Assuming you have progressbar.js installed

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const progressRef = useRef(null); // Reference for the progress bar element

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', selectedFile);

    const config = {
      onUploadProgress: (progressEvent) => {
        const uploadProgress = Math.round((progressEvent.loaded * 100) / progressEvent.total);
        console.log('Upload progress:', uploadProgress);
        progressRef.current.animate(uploadProgress / 100); // Update progress bar based on upload progress
      },
    };

    axios.post('http://localhost:5000/upload', formData, config)
      .then(response => {
        setLoading(false);
        progressRef.current.animate(1.0); // Animate to 100% on successful upload
        console.log('Upload successful:', response.data);
        // Redirect to summary page with the summary data
        window.location.href = `/summary?summary=${response.data.summary}`;
      })
      .catch(error => {
        console.error('Error:', error);
        setLoading(false);
        alert('File upload failed!');
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Briefly AI</h1>
        <p>Effortlessly Summarize</p>
      </header>
      <div className="button-container">
        <input
          type="file"
          id="fileInput"
          className="file-input"
          onChange={handleFileChange}
        />
        <label htmlFor="fileInput" className="button">Choose File</label>
        <button className="button" onClick={handleUpload} disabled={!selectedFile}>
          Upload File
        </button>
      </div>
      {selectedFile && (
        <p className="filename">Selected File: {selectedFile.name}</p>
      )}
      {loading && (
        <div ref={progressRef} className="loading-bar"></div>
      )}
    </div>
  );
}

export default App;
