import React from 'react';
import './App.css';

function handleButtonClick() {
  // Get the current tab's dimensions
  chrome.tabs.query({ active: true, currentWindow: true }, function(tabs) {
    const tab = tabs[0];
    const tabWidth = tab.width;

    // Calculate the new dimensions based on the provided values
    const newX = -2114;
    const newY = -2182;
    const newWidth = Math.round(tabWidth * 0.15); // 15% of the tab's width
    const newHeight = 2941;

    // Open a new window with the calculated dimensions
    chrome.windows.create({
      url: 'popup.html', // Change this to the URL of your popup HTML file
      left: newX,
      top: newY,
      width: newWidth,
      height: newHeight,
      type: 'popup'
    });
  });
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <h1>Briefly AI</h1>
        <p1>Effortlessly Summarize</p1>
      </header>
      <div>
        <button className="button" onClick={handleButtonClick}>Upload File</button>
      </div>
    </div>
  );
}

export default App;
