import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import UploadButton from './UploadButton'; // Import the UploadButton component

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await axios.post('http://localhost:5000/upload', formData);

      setLoading(false);
      window.location.href = `/summary?summary=${response.data.summary}`;
    } catch (error) {
      console.error('Error:', error);
      setLoading(false);
      alert('File upload failed!');
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Briefly AI</h1>
        <p>Effortlessly Summarize</p>
      </header>
      <div className="button-container">
        <input
          type="file"
          id="fileInput"
          className="file-input"
          onChange={handleFileChange}
        />
        <label htmlFor="fileInput">Choose File</label>
        <UploadButton handleUpload={handleUpload} />
      </div>
      {selectedFile && (
        <p className="filename">Selected File: {selectedFile.name}</p>
      )}
      {loading && <div className="loading">Uploading...</div>}
    </div>
  );
}

export default App;
