import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (!selectedFile) {
      alert('Please select a file first.');
      return;
    }

    setLoading(true);

    const formData = new FormData();
    formData.append('file', selectedFile);

    axios.post('http://localhost:5000/upload', formData)
      .then(response => {
        const { summary } = response.data;
        localStorage.setItem('summary', summary); // Store summary in local storage
        setLoading(false);
        navigate('/summary'); // Navigate to the summary page
      })
      .catch(error => {
        console.error('Error:', error);
        alert('File upload failed!');
        setLoading(false);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Briefly AI</h1>
        <p>Effortlessly Summarize</p>
      </header>
      <div className="button-container">
        <input
          type="file"
          id="fileInput"
          className="file-input"
          onChange={handleFileChange}
        />
        <button className="button" onClick={() => document.getElementById('fileInput').click()}>
          Choose File
        </button>
        {selectedFile && (
          <button className="button" onClick={handleUpload}>
            Upload File
          </button>
        )}
      </div>
      {loading && <div className="loading">Loading...</div>}
    </div>
  );
}

export default App;
